	(31, 'Bilecik Şeyh Edebali Üniversitesi', 'Bilecik Şeyh Edebali Üniversitesi Rektörlüğü 11210 Gülümbe/BİLECİK', 'http://www.xsorgula.com/files/news/thumb/news-3bbf129374b65eaec997.jpg', 'http://www.bilecik.edu.tr/', 'Bilecik Şeyh Edebali Üniversitesi 29 Mayıs 2007 tarihinde, eğitim-öğretim ve bilimsel araştırmalar konusunda tercih edilen, kurumsal kimliği ve kültürü gelişmiş bir üniversite olarak bilim dünyasına katkı sağlama vizyonuyla Bilecik il sınırları içerisinde devlet üniversitesi olarak kurulmuştur. Merkez kampüsü Gülümbe\'de bulunan üniversite meslek yüksekokulları ile Bilecik\'in ilçelerine yayılım göstermiştir. İlim ve fen için kayıt ve şart yoktur sloganı ışığında eğitim faaliyetlerini sürdüren üniversitenin bünyesinde 5 fakülte, 2 enstitü, 2 yüksekokul ve 7 meslek yüksekokulu bulunmaktadır. Bu eğitim birimlerinde 13863 öğrenci öğrenim görmekte, 455 akademik personel de bu birimlerde çalışmalarını sürdürmektedir.', 'bilecik-seyh-edebali-universitesi', NULL, NULL, 'Bilecik', NULL, '/m/0r8m1bg');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(32, 'Bingöl Üniversitesi', 'Selahaddin-i Eyyubi Mah. Aydınlık Cad. No: 1 Posta Kodu: 12000, Bingöl/TÜRKİYE', 'http://www.bingol.edu.tr/documents/file/bu_BilgolUniverstyLogo/5-300-b8a17557-c808-48cf-9ffe-e0c8098ee96a.jpg', 'http://www.bingol.edu.tr', 'Bingöl Üniversitesi 29 Mayıs 2007 tarihinde Bingöl il sınırları içerisinde bilimsel faaliyetleriyle birlikte, içinde bulunduğu toplumun gerçeklerinin farkında ve değerleriyle barışık olarak toplumla güçlü ilişkiler kuran, bölgesel kaynakları en ideal biçimde kullanıp sürdürülebilir kalkınmayla birlikte insan odaklı rekabet gücünü artıran, yükseköğretimde çağın gereklerine yönelik adımlar atan ve bilimsel değerleri kendine rehber edinen ve özümseyen bir üniversite olma vizyonuyla devlet üniversitesi olarak kurulmuştur.Tek bir ana yerleşkede hizmet veren üniversitenin bünyesinde 7 Fakülte, 3 Yüksekokul, 5 Meslek Yüksekokulu ve 4 Enstitü birimi bulunmaktadır. Bu birimlerde 11065 öğrenci öğrenim görmekte, 484 akademik personel de çalışmalarını yürütmektedir.', 'bingol-universitesi', NULL, NULL, 'Bingöl', NULL, '/m/0r8lt74');
