INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(44, 'Dicle Üniversitesi', 'Dicle Üniversitesi Rektörlüğü, 21280-Diyarbakır.', 'http://www.dicle.edu.tr/Contents/c1dcaadc-b976-499a-84e7-d71871668193.gif', 'http://www.dicle.edu.tr', 'Dicle Üniversitesi 1974 yılında bölgesine, ülkesine ve evrensel bilime katkıda bulunacak bir araştırma üniversitesi olma vizyonu ile Diyarbakır il sınırları içerisinde devlet üniversitesi statüsünde kurulmuştur. Dicle Üniversitesi bünyesinde 14 fakülte, 4 yüksekokul, 11 meslek yüksekokulu ve 4 enstitüsü, teknokent, 24 Araştırma ve Uygulama Merkezi bulunmaktadır. Bu birimlerde faaliyetlerini yürüten 3600 civarında kadrolu akademik ve idari elemanı ile Dicle Üniversitesi, yaklaşık olarak 30.000 öğrenciye evrensel değerleri temel alarak yaptığı eğitim, öğretim ve araştırma faaliyetleriyle her alanda yetkin, yaratıcı, girişimci, ahlaki değer ve toplumsal sorumluluk sahibi üstün nitelikli bireyler yetiştirme, topluma ve çevreye duyarlı yüksek nitelikli bilimsel ve kültürel çalışmalar yapma ve insanlığın yararına sunma amacıyla eğitim vermektedir.', 'dicle-universitesi', NULL, NULL, 'Diyarbakır', NULL, '/m/0ds0828');
