INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(50, 'Erciyes Üniversitesi', 'Erciyes Üniversitesi Rektörlüğü 38039 Kayseri - Türkiye', 'http://guzelsanat.erciyes.edu.tr/logolar/e_u_logo_jpg_96dpi_RGB.jpg', 'http://en.erciyes.edu.tr/', 'Erciyes Üniversitesi 1978 yılında Kayseri\'de devlet üniversitesi statüsünde bilimsel çalışmalara, bilgi ve teknoloji üretimine ve sosyal, kültürel, sanat ile spor alanlarına katkı sağlayan; uluslararası nitelikte öğrenci ve öğretim elemanı yapısına sahip; bünyesinde Türkiye’de önde gelen ve Dünya\'da saygın araştırma merkezleri bulunduran; yerel, evrensel ve etik değerlere sahip öncü bir üniversite olma vizyonuyla Kayseri\'de kurulmuştur. Kayseri Merkez Kampüsü, Seyrani, Tomarza olmak üzere 3 yerleşkesi bulunan üniversitenin bünyesinde 18 fakülte, 3 yüksekokul, 9 meslek yüksekokulu, 7 enstitü, 6 bölüm, 38 araştırma merkezi faaliyet göstermektedir. Üniversitede, 2014 - 2015 öğretim yılı itibariyle 2300\'e yakın akademik personel görev yapmakta olup yaklaşık 50.000 öğrenci de eğitim almaktadır.', 'erciyes-universitesi', '35.5235473', '38.7005635', 'Kayseri', NULL, '/m/09yxhj');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(51, 'Erzincan Üniversitesi', 'Erzincan Üniversitesi Rektörlüğü Fatih Mahallesi 24100-ERZİNCAN', 'http://eski.erzincan.edu.tr/userfiles/menu/images/Erznc_%20%DCni.jpg', 'http://www.erzincan.edu.tr', 'Erzincan Üniversitesi 11.07.1992 tarihinde Anadolu’da bir dünya üniversitesi olma vizyonuyla Erzincan il sınırları içerisinde kurulmuştur. Erzincan merkezde bulunan rektörlük binası, Yalnızbağ Yerleşkesi, meslek yüksekokulu ve yüksekokul binaları ile üniversitenin yerleşkeleri Erzincan il sınırları içerisinde dağılım göstermiştir. Üniversite bünyesinde 9 fakülte, 3 enstitü, 5 yüksekokul, 11 meslek yüksekokulu ve 20 araştırma ve uygulama merkezi bulunmaktadır. Bu birimlerde 19.500 öğrenci öğrenim görmektedir. 855 Akademik Personel, 372 İdari Personeli ile Erzincan Üniversitesi, insan merkezli bir eğitim anlayışıyla, bilimi ve aklı esas alarak ülkemizin değerleri ve hedefleri doğrultusunda üstün nitelikli bireyler yetiştirme; uluslararası ölçekte bilim ve teknoloji üreterek bölgenin ve ülkenin sürdürülebilir kalkınmasına katkıda bulunma ve toplumun sorunlarını çözmeye yönelik çalışmalar yapma misyonuyla eğitim hayatını sürdürmektedir.', 'erzincan-universitesi', NULL, NULL, 'Erzincan', NULL, '/m/0br_gkk');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(52, 'Erzurum Teknik Üniversitesi', 'Çat Yolu Üzeri 4. Km / Erzurum', 'http://www.tasarimyarismalari.com/wp-content/uploads/2012/05/etu-logo-3.jpg', 'http://www.erzurum.edu.tr/', 'Erzurum Teknik Üniversitesi, Erzurum’da ikinci bir devlet üniversitesi olarak 21.07.2010 tarihinde toplumla iletişim halinde bulunarak bölgenin problemlerine bilimsel çözümler üretecek sosyal ve ekonomik çalışmalar yapan, yaşam boyu öğrenmeyi temel ilke kabul ederek bilginin ve sanatın tüm toplum katmanlarına yayılmasına katkıda bulunan ve bölgesinde yüksek teknoloji konularında araştırma altyapısı eksikliğini gidererek ileri teknoloji konularında araştırma üssü olmayı hedefleyen bir Dünya Üniversitesi olma amacıyla kurulmuştur. Çat yolu 4. Km\'deki rektörlük binasının yanı sıra Havalimanı Yolu Üzeri\'ndeki derslikler binası ile çalışmalarını yürüten üniversitenin merkez kampüs inşaatı sürmektedir. 6 fakülte, 3 enstitü ve 1 yüksekokulun bulunduğu üniversitede 173 akademik personel görev almakta, 1835 öğrenci öğrenim görmektedir.', 'erzurum-teknik-universitesi', NULL, NULL, 'Erzurum', NULL, '/m/0r8nn3k');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(53, 'Eskişehir Osmangazi Üniversitesi', 'Meşelik Kampüsü, Atatürk Bulv., Osmangazi, 26480 Eskişehir, Türkiye', 'http://yos.ogu.edu.tr/images/logo.png', 'http://www.ogu.edu.tr', 'Eskişehir Osmangazi Üniversitesi, 18 Ağustos 1993\'de Eskişehir\'de Ulusal ve evrensel değerlere sahip, bilim ışığında araştırma-geliştirme yapabilen, girişimci, katılımcı, paylaşımcı, bilim ve teknoloji üreterek topluma katkı sağlayan mezunlar ve bilim insanları yetiştiren, çağdaş hizmetler sunan, çalışanlarının haklarını koruyan ve onlara saygılı, ulusal ve uluslararası düzeyde saygınlığı olan, alanında öncülük eden ve çalışmaları örnek alınan bir üniversite olma vizyonu ile Eskişehir\'de kurulmuştur. Merkez yerleşkesi Meşelik olan üniversitenin, Bademlik, Çamlık, Ali Numan Kıraç, Organize Sanayi Bölgesi, Sivrihisar, Mahmudiye olmak üzere 7 yerleşkesi bulunmaktadır. Üniversite bünyesinde 10 fakülte, 2 yüksekokul, 4 meslek yüksekokulu, 5 enstitü, 27 araştırma ve uygulama merkezi bulunmaktadır. Bu birimlerde 29.000 öğrenci öğrenim görmektedir.', 'eskisehir-osmangazi-universitesi', '30.4849402', '39.7510832', 'Eskişehir', NULL, '/m/02qfpv5');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(54, 'Fırat Üniversitesi', '', 'http://logovector.org/wp-content/uploads/logos/png/f/firat_universitesi_logo.png', 'http://www.firat.edu.tr/', 'Fırat Üniversitesi 1975 yılında bilimin parlayan yıldızı sloganıyla Elazığ\'da devlet üniversitesi olarak kurulmuştur. Elazığ Merkez\'de bulunan kampüsü ve çevre ilçelerindeki meslek yüksek okulları ile faaliyetlerini sürdürmektedir. 36091 öğrencinin öğrenim gördüğü üniversite bünyesinde 14 Fakülte, 4 Yüksekokul, 1 Devlet Konservatuarı, 9 Meslek Yüksekokulu, 4 Enstitü, 23 araştırma ve uygulama merkezi ve bu birimlerde akademik faaliyetlerini sürdüten 1697 öğretim üyesi bulunmaktadır.', 'frat-universitesi', '39.2054067', '38.6776247', 'Elazığ', NULL, '/m/03mgm2_');
