INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(56, 'Gazi Üniversitesi', 'Gazi Üniversitesi Rektörlüğü, 06500 Teknikokullar ANKARA', 'http://www.freelogovectors.net/wp-content/uploads/2013/04/gazi_universitesi_logo.jpg', 'http://gazi.edu.tr/', 'Gazi Üniversitesi, Cumhuriyet tarihinin ilk üniversitesi olarak 1926 yılında Ankara\'da devlet üniversitesi olarak, bilim ve teknolojiden sanat ve spora kadar uzanan geniş bir yelpazede özgün araştırmaları, üst düzey eğitimi, yaratıcı faaliyetleri ve topluma hizmeti ile ulusal ve uluslararası düzeyde saygın ve lider bir üniversite olma vizyonuyla kuruldu. Rektörlük binasının bulunduğu Merkez Yerleşkesi ile beraber Beşevler, Emek, Maltepe, Gölbaşı, Çubuk, Ostim ve Polatlı olmak üzere toplam 8 yerleşkesi bulunan, 77 bin öğrencinin öğrenim gördüğü üniversite 21 fakültesi, 4 yüksekokulu, 1 Türk Müziği Devlet Konservatuvarı, 11 meslek yüksekokulu, 48 araştırma merkezi ve 7 enstitüsü bünyesinde çalışan 4000 akademik personeli ile Gazi Üniversitesi adını aldığı Gazi Mustafa Kemal Atatürk\'e yaraşır bir yüksek öğretim kurumu olma iddiasını devam ettirmektedir.', 'gazi-universitesi', '32.8225332', '39.9400862', 'Ankara', NULL, '/m/07h638');
