	(100, 'Namık Kemal Üniversitesi', 'Namık Kemal Mahallesi Kampüs Caddesi Merkez/Tekirdağ', 'http://logovector.org/wp-content/uploads/logos/png/n/namik_kemal_universitesi_logo.png', 'http://www.nku.edu.tr', 'Namık Kemal Üniversitesi 17 Mart 2006 tarihinde uluslararası standartlarda eğitim-öğretim hizmeti vererek, araştırmacı ve yenilikçi anlayışıyla sürekli gelişen bir dünya üniversitesi olma vizyonuyla Tekirdağ\'da devlet üniversitesi statüsünde kurulmuştur. Üniversitenin merkez kampüsü Değirmenaltı kampüsüdür. 26.500 öğrencinin öğrenim gördüğü 8 Fakülte, 1 Konservatuvar, 3 Enstitü, 3 Yüksekokul, 11 Meslek Yüksekokulu, 10 Uygulama ve Araştırma Merkezi ve 5 Bölüm Başkanlığı\'nda 1.324 akademik ve idari personel bulunmaktadır.', 'namk-kemal-universitesi', '27.5820928', '40.9927858', 'Tekirdağ', NULL, '/m/0cpbvt1');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(101, 'Necmettin Erbakan Üniversitesi', 'Necmettin Erbakan Üniversitesi Rektörlüğü Nişantaşı Mah. Dr. Hulusi Baybal Cad. No:12 Kat:25 Posta Kodu:42060 Selçuklu / KONYA', 'http://akademikduyuru.com/wp-content/uploads/2016/06/Necmettin-Erbakan-%C3%9Cni-Logo.png', 'http://www.konya.edu.tr', 'Necmettin Erbakan Üniversitesi 14.07.2010\'da katılımcı, farklılıklara saygılı ve özgürlükçü bir kurum kültürü içinde tüm paydaşlarımızın gereksinimlerine duyarlı olma prensibini esas alarak, araştırma ve eğitim alanındaki yenilikçi ve yol gösterici üslubu ile öncülüğe yol açan, özgün bilginin ortaya çıkarılmasını ve yayılmasını sağlayan, uluslararası bir referans noktası olma vizyonuyla Konya\'da devlet üniversitesi statüsünde kurulmuştur. 27361 öğrencinin öğrenim gördüğü 14 fakülte, 5 yüksekokul, 4 enstitü, 6 koordinatörlük ve 13 araştırma merkezinde 1.150 idari personel ve 1.459 akademik personel görev almaktadır.', 'necmettin-erbakan-universitesi', NULL, NULL, 'Konya', NULL, '/g/1hb_gpz0r');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(102, 'Nevşehir Hacı Bektaş Veli Üniversitesi', '2000 Evler, Nevşehir Fakülte Yolu, 50300 Nevşehir Merkez/Nevşehir, Türkiye', 'http://dosyalar.nevsehir.edu.tr/7c0b4dafdc489938bd8b87b3bc0e2661_t/5x5cm_jpeg.jpg', 'http://www.nevsehir.edu.tr/', 'Nevşehir Hacı Bektaş Veli Üniversitesi 2007 yılında Dünyanın kültürel ve doğal miraslarından biri olan Kapadokya Bölgesinde eğitim, araştırma ve entelektüel liderliğiyle turizm, ekonomi, sanat ve teknoloji alanlarında topluma hizmet sunarak yerelden kopmadan evrensel değerlere katkıda bulunan öncü bir üniversite olma vizyonuyla devlet üniversitesi statüsünde Nevşehir\'de kurulmuştur. Üniversitenin merkez yerleşkesi Nevşehir\'de bulunmaktadır. Bunun dışında üniversite Avanos, Derinkuyu, Gülşehir, Hacıbektaş, Kozaklı, Ürgüp ilçelerinde bulunan yerleşkelerinde de eğitim vermektedir. 14609 öğrencinin öğrenim gördüğü üniversite bünyesinde bulunan 7 fakülte, 2 yüksekokul, 6 meslek yüksekokulu ve 7 araştırma merkezinde 575 akademik personel çalışmalarını yürütmektedir.', 'nevsehir-hac-bektas-veli-universitesi', NULL, NULL, 'Nevşehir', NULL, '/m/0j7m692');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(103, 'Niğde Üniversitesi', 'Niğde Üniversitesi Rektörlüğü, Merkez Yerleşke, Bor Yolu Üzeri, Niğde, 51240', 'http://medya.nigde.edu.tr/uniweb/media/sayfa/logo/Nigdeuniversitesi.jpg', 'http://www.nigde.edu.tr/', 'Niğde Üniversitesi Bilim ve teknoloji alanlarında rekabet gücüne sahip bir üniversite olma vizyonuyla Niğde\'de devlet üniversitesi olarak kurulmuştur. Merkez yerleşkesinin Bor Yolu üzerinde konumlandığı, 23.900 öğrencinin öğrenim gördüğü 9 fakülte, 3 enstitü, 3 yüksekokul, 6 meslek yüksekokulu, 1 devlet konservatuvarı, rektörlüğe bağlı 4 bölüm başkanlığı, 1 kariyer geliştirme merkezi, 9 araştırma ve uygulama merkezi ve1 sürekli eğitim merkezinde 830 akademik personel görev almaktadır.', 'nigde-universitesi', '34.623765', '37.9420419', 'Niğde', NULL, '/m/0g9wyq8');
