INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(115, 'Şırnak Üniversitesi', 'ŞIRNAK ÜNİVERSİTESİ Cumhuriyet Mahallesi Atatürk Caddesi 73000 Şırnak - Türkiye', 'http://www.sirnak.edu.tr/resimler/images/logo800x571.jpg?phpMyAdmin=dd7d1a81162a2c8bfbed1c7bf88976cb', 'http://www.sirnak.edu.tr', 'Şırnak Üniversitesi 22 Mayıs 2008 tarihinde Ulusal ve bölgesel sorunların çözümüne odaklı, yenilikçi, kalite anlayışını benimsemiş saygın bir üniversite olma vizyonuyla devlet üniversitesi statüsüyle Şırnak\'ta kurulmuştur. 3522 öğrencinin öğrenim üniversitenin bünyesinde bulunan 4 fakülte, 1 enstitü, 2 yüksekokul, 4 yüksekokulda Şırnak Üniversitesi, evrensel bakış açısıyla bilgi ve teknoloji üretmek; ürettiği bilgi ve teknolojiyi bölgenin ve ülkenin kalkınması yararına kullanmak; ulusal ve uluslararası işbirliği ve dayanışma anlayışıyla toplumun yaşam kalitesinin yükseltilmesine yönelik eğitim ve araştırma faaliyetlerine öncülük etmek; ülkenin ihtiyaç duyduğu araştırmacı, paylaşımcı, sorgulayıcı, çözümleyici, girişimci, yaşam boyu öğrenmeyi benimsemiş, kültürlü ve estetik değerlere sahip bireyler yetiştirme misyonuyla eğitim ve öğretim faaliyetlerine 272 akademik personel ile devam etmektedir.', 'srnak-universitesi', NULL, NULL, 'Şırnak', NULL, '/g/120trzgp');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(116, 'Süleyman Demirel Üniversitesi', 'Süleyman Demirel Üniversitesi, 32260 Çünür/ISPARTA', 'http://w3.sdu.edu.tr/kurumsal/sdu_logo.jpg', 'http://www.sdu.edu.tr/', 'Süleyman Demirel Üniversitesi 1992 yılında Isparta\'da eğitim-öğretim, bilim ve araştırmada kaliteden ödün vermeden emin adımlarla geleceğe yürüyerek, bilimsel çalışmalarıyla dünyaya şekil vererek, içinde bulunulan yörenin toplumsal değerlerine saygılı, yereli iyi bilen, ülke sorunlarına duyarlı, bölgesinde lider, bununla birlikte bir Dünya üniversitesi olarak gelişmesini sürdürme vizyonuyla kurulan bir devlet üniversitesidir. Üniversite binalarının büyük bir kısmı Isparta’daki merkez yerleşkededir. Süleyman Demirel Üniversitesi; 19 fakülte, 3 yüksekokul, 20 meslek yüksekokulu, 6 enstitü ile 46 araştırma ve uygulama merkezine ve burada öğrenim gören 55.293 öğrenciye sahip olup modern eğitim olanakları ve güçlü akademik kadrosuyla öğrencilerine kucak açan, öğrencilerinin çeşitli meslek dallarında en iyi eğitimi almalarını amaç edinmiştir.', 'suleyman-demirel-universitesi', '30.5295082', '37.8324966', 'Isparta', NULL, '/m/0f04kj');
