INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(132, 'Bahçeşehir Üniversitesi', '', 'http://www.dgsli.org/wp-content/uploads/2014/08/Bah%C3%A7e%C5%9Fehir-%C3%9Cniversitesi_logo.jpg', 'http://www.bahcesehir.edu.tr', 'Bahçeşehir Üniversitesi, 1998 yılında Bahçeşehir-Uğur Eğitim Vakfı tarafından “İstanbul\'un Kalbinde Bir Dünya Üniversitesi” sloganıyla açılan bir vakıf üniversitesidir. Merkez kampüsünün Beşiktaş\'ta olduğu Bahçeşehir Üniversitesi\'nin İstanbul\'daki diğer kampüsleri Galata, Göztepe ve Şişli\'de bulunmaktadır. Bunun dışında Bahçeşehir Üniversitesi\'nin yurt dışında Berlin,Silikon Vadisi, Hong Kong, Köln, Batum ve Toronto’da olmak üzere toplam 6 kampüsü daha bulunmaktadır. 8 fakülte, 1 yüksekokul, 2 meslek yüksekokulu ve 14 araştırma merkezinin faaliyet gösterdiği Bahçeşehir Üniversitesi\'nde 1047 akademik personel, 506 idari personel görev almakta ve 17.048 öğrenci öğrenim görmektedir.', 'bahcesehir-universitesi', NULL, NULL, 'İstanbul', NULL, '/m/0bq1kt');
