INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(134, 'Beykent Üniversitesi', 'Taksim Yerleşkesi, Sıraselviler, 34433 Beyoğlu/İstanbul, Türkiye', 'http://beykent.edu.tr/uploads/ckeditor/attachments/561ce2a76565730abf030000/BeykentLogo_ENG_RGB.png', 'http://www.beykent.edu.tr', 'Beykent Üniversitesi, 1997 yılında İstanbul\'da profesyonel yaşam için doğru seçim sloganıyla kurulmuş bir vakıf üniversitesidir. Ayazağa-Maslak, Beylikdüzü ve Taksim olmak üzere üç yerleşkede eğitim faaliyetlerini sürdüren üniversitenin bünyesinde 9 fakülte, 1 meslek yüksekokulu ve 2 enstitü bulunmaktadır. 500 öğretim üyesinin görev yaptığı bu birimlerde 25.000 öğrenci öğrenim görmektedir.', 'beykent-universitesi', '29.0040176', '41.1172697', 'İstanbul', NULL, '/m/03cc49z');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(135, 'Bezm-i Âlem Vakıf Üniversitesi', 'Adnan Menderes Bulvarı Vatan Caddesi 34093 Fatih/İstanbul', 'http://www.ohder.org.tr/wp-content/uploads/2015/11/logo.png.jpg', 'http://www.bezmialem.edu.tr', 'Bezmi Alem Vakıf Üniversitesi, Bezm-i Alem Valide Sultan Vakıf Gureba tarafından 2010\'da vakıf üniversitesi statüsünde kurulmuştur. Sağlık bilimleri üzerine eğitim veren üniversitenin merkez yerleşkesi İstanbul\'un Fatih ilçesindedir. Yine aynı şehirde üniversitenin Gayrettepe kampüsü bulunmaktadır. Eğitimde örnek alınan, bilime yön veren, lider sağlık üniversitesi olma vizyonuyla hareket eden üniversite bünyesinde 4 fakülte, 1 meslek yüksekokulu, 4 enstitü, 2 kurul ve 2 araştırma merkezi bulunmaktadır. 2067 öğrencinin öğrenim gördüğü üniversitede 483 akademik personel çalışmalarını sürdürmektedir.', 'bezm-i-alem-vakf-universitesi', NULL, NULL, 'İstanbul', NULL, NULL);
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(136, 'Biruni Üniversitesi', '10. Yıl Caddesi Protokol Yolu No: 45, 34010 Topkapı İstanbul', 'http://obs.biruni.edu.tr/files/university/images/logo.png', 'http://www.biruni.edu.tr/', 'Biruni Üniversitesi 27.02.2014 tarihinde İstanbul\'da kurulmuş bir vakıf üniversitesidir. Topkapı\'da bulunan kampüsünde kurulduğu günden bugüne eğitim, araştırma ve hizmetleri ile ülkemiz ve Dünya’da referans kabul edilen yenilikçi, girişimci lider bir sağlık üniversitesi olma vizyonuyla çalışmalarını sürdürmektedir. Bünyesinde bulunan 6 fakülte 3 enstitü, 1 meslek yüksek okulu ve 1 araştırma merkezinde sağlığın her alanında bilgi üreten ve uygulama sorumluluğu taşıyan, öğrenci odaklı, toplumun değer yargılarına ve farklılıklarına saygılı, dünya standartlarında eğitim ve hizmet verme misyonuyla 4 binden fazla öğrenciye eğitim-öğretim vermektedir.', 'biruni-universitesi', NULL, NULL, 'İstanbul', NULL, '/g/1q5jpwm52');
