INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(138, 'Çankaya Üniversitesi', 'Eskişehir Yolu 29. Km, Yukarıyurtçu Mahallesi Mimar Sinan Caddesi No:4 06790, Etimesgut/ANKARA', 'http://www.tto.cankaya.edu.tr/wp-content/uploads/2015/05/Logo_of_%C3%87ankaya_University.png', 'http://www.cankaya.edu.tr/', 'Çankaya Üniversitesi, Sıtkı Alp Eğitim Vakfı tarafından Dünya standartlarında kaliteli yükseköğretim ve uluslararası yabancı dilde eğitim sağlayan; uluslararası düzeyde araştırma-geliştirme çalışmaları yapan ve bilgi üreten; topluma hizmeti temel görevleri arasında sayan; bilgi toplumunun oluşmasında türkiyemizde öncülük eden; Türkiye Cumhuriyeti’nin demokratik, laik ve sosyal hukuk devleti niteliklerine sahip çıkan; vatanını, milletini ve tüm insanları seven bireyler yetiştiren; üniversite-sanayi işbirliğine önem veren; toplumun her kesiminde saygı duyulan, bir üniversite olmak amacıyla 1997-1998 öğretim yılında Ankara\'da faaliyete geçmiştir.Biri Ankara Balgat\'ta diğeri Eskişehir yolu üzerinde iki kampüsü bulunan üniversitenin 5 fakülte, 21 bölüm, 2 meslek yüksekokulu ve 2 enstitüsü bulunmaktadır. 286 akademisyenin çalışmalarını yürüttüğü bu birimlerde 7068 öğrenci öğrenim görmektedir.', 'cankaya-universitesi', '32.5643239', '39.8192699', 'Ankara', NULL, '/m/08ngh6');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(139, 'Doğuş Üniversitesi', 'Acıbadem, Kadıköy, 34722 İstanbul', 'http://www.patentvadisi.gov.tr/patentvadisi/uploads/105dogus_logo.jpg', 'http://www.dogus.edu.tr', 'Doğuş Üniversitesi, Doğuş Eğitim Vakfı tarafından 1997 yılında İstanbul\'da eğitim-öğretimde, araştırmada ve topluma hizmette mükemmele ulaşmış bir dünya üniversitesi olma amacıyla kurulmuş bir vakıf üniversitesidir. Kadıköy’de Acıbadem Mahallesi’nde bulunan yerleşkesinde ülkemizin bilim, teknoloji, sanat ve kültür alanındaki gelişmelerinde öncü rol üstlenme ve bu alanlarda insanlığın yararına çalışmalar yürütme misyonuyla eğitim veren Doğuş Üniversitesi bünyesinde 5 fakülte, 2 enstitü, 1 meslek yüksekokulu ve 1 sürekli eğitim merkezi bulunmaktadır. Bu birimlerde 182 akademisyen görev almakta, 5518 öğrenci de öğrenim görmektedir.', 'dogus-universitesi', '29.0471269', '41.0004024', 'İstanbul', NULL, '/m/02qgp_m');
