INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(153, 'İstanbul Esenyurt Üniversitesi', 'Doğan Araslı Bulv. No:79 Esenyurt, İstanbul / Türkiye', NULL, 'http://www.esenyurt.edu.tr', 'İstanbul Esenyurt Üniversitesi, Yeşilköy 2001 Eğitim, Sağlık, Kültür Vakfı tarafından Özgürlükçü, akademik ve fiziki altyapısı ile öne çıkan, geleneklere ve farklılığa saygılı,Ülkesine ve bilime hizmeti amaç edinmiş, disiplinler arası ve dünya standartlarında bir eğitim anlayışı ile özgün bilgiler yaratılmasını sağlayan, geleceğe yön verebilecek bireyleri yetiştiren öncü bir dünya üniversitesi olma amacıyla İstanbul\'da vakıf üniversitesi statüsüyle kurulmuştur.180 akademik personelin çalışma yürüttüğü Üniversite bünyesinde 4 fakülte, 2 yüksekokulu, 2 meslek yüksekokulu 3 enstitü ve bu birimlerde de okuyan 3667 öğrenci bulunmaktadır.', 'istanbul-esenyurt-universitesi', NULL, NULL, 'İstanbul', NULL, '/g/1s067rlyt');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(154, 'İstanbul Gelişim Üniversitesi', 'Cihangir mah. Şehit Jandarma Komando Er Hakan Öner Sk. No:1 Avcılar / İSTANBUL', NULL, 'http://www.gelisim.edu.tr', 'İstanbul Gelişim Üniversitesi, Gelişim Eğitim ve Kültür Vakfı tarafından 14.7.2008 tarihinde yüksekokul olarak kurulmuştur. 17.02.2011 tarihine gelindiğinde ise vakıf üniversitesi statüsünde eğitime başlamıştır. Üniversite Avcılar\'da bulunan Cihangir Mahallesi\'ndeki yerleşkesini merkez yerleşke olarak belirlemiştir. Üniversitenin Avcılar yan yol üzerinde bir meslek yüksekokulu yerleşkesi de bulunmaktadır. Üniversite, 3 fakültesi, 3 yüksekokulu, 2 meslek yüksekokulu ve 3 enstitüsünde yaklaşık 170 bölümle, 20 bin öğrenciye 394 akademik personeli ile eğitim vermektedir.', 'istanbul-gelisim-universitesi', NULL, NULL, 'İstanbul', NULL, '/g/1hb_dh4lr');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(155, 'İstanbul Kemerburgaz Üniversitesi', 'Mahmutbey Dilmenler Caddesi,   No:26, 34217 Bağcılar - İSTANBUL', NULL, 'http://www.kemerburgaz.edu.tr', 'İstanbul Kemerburgaz Üniversitesi 2008 yılında, Mehmet Altınbaş Eğitim ve Kültür Vakfı tarafından kurulmuş İstanbul\'da bilim farkıyla önde sloganıyla vakıf üniversitesi olarak kurulmuştur. Üniversite ilk öğrencilerini 2011-2012 akademik yılında alarak eğitim-öğretim hayatına başlamıştır. Üniversitenin Şişli ve Mahmutbey olmak üzere 2 yerleşkesi bulunmaktadır. 3861 öğrencinin öğrenim gördüğü üniversitenin 8 fakülte, 3 enstitü ve 2 meslek yüksekokulunda 310 akademik personel görev yapmaktadır.', 'istanbul-kemerburgaz-universitesi', NULL, NULL, 'İstanbul', NULL, '/m/0pdny5h');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(156, 'İstanbul Kültür Üniversitesi', 'E5 Karayolu üzeri Bakırköy 34156 İstanbul', NULL, 'http://www.iku.edu.tr', 'İstanbul Kültür Üniversitesi 15 Temmuz 1997 tarihinde Türkiye Cumhuriyeti Devleti’nin ve Türk milletinin "çağdaş medeniyet seviyesini aşma" yolunda değişmez hedefine ulaşması, gençlerin, hangi aşamada olursa olsun, nitelikli bir eğitim almaları ile ancak mümkün olduğundan hareketle kaliteli yükseköğretim yapma amacı ile İstanbul\'da vakıf üniversitesi statüsünde kurulmuştur. Kültür Üniversitesi\'nin Ataköy, Şirinevler ve İncirli olmak üzere 3 yerleşkesi bulunmaktadır. 10743 öğrencinin öğrenim gördüğü üniversitede bulunan 7 fakülte, 3 meslek yüksekokulu, 2 enstitü ve 10 araştırma merkezinde 611 akademik personel görev yapmaktadır.', 'istanbul-kultur-universitesi', NULL, NULL, 'İstanbul', NULL, '/m/0ch4_4d');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(157, 'İstanbul Medipol Üniversitesi', 'Unkapanı, Atatürk Bulvarı No:27 34083 Fatih-İstanbul', NULL, 'http://www.medipol.edu.tr/', 'İstanbul Medipol Üniversitesi, Türkiye Eğitim Sağlık ve Araştırma Vakfı tarafından 7 Temmuz 2009 tarihinde İstanbul\'da güçlü kurumsal kültürü, girişimcilik ruhu ve sürekli gelişime açık dinamizmi ile bilime ve topluma yön veren öncü bir üniversite olma vizyonuyla vakıf üniversitesi olarak kurulmuştur. Üniversite rektörlüğün bulunduğu Haliç Yerleşkesi ve diğer yerleşkesi olan Kavacık Yerleşkesi\'nde konumlanmıştır. 4942 öğrencinin öğrenim gördüğü üniversitenin 11 Fakülte, 1 yüksekokul, 4 meslek yüksekokulu, 4 enstitü ve 7 araştırma ve uygulama merkezinde 683 akademik personel görev almaktadır.', 'istanbul-medipol-universitesi', NULL, NULL, 'İstanbul', NULL, '/g/1hb_fvc0h');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(158, 'İstanbul Rumeli Üniversitesi', 'Yeni Mah. Mehmet Silivri Cad. No: 38 Silivri - İstanbul / TÜRKİYE', NULL, 'http://www.rumeli.edu.tr/', 'İstanbul Rumeli Üniversitesi, 3 Fakülte, 4 Meslek Yüksekokulu, 2 Enstitü ile İstanbul\'da, Silivri, Çatalca, Esenyurt ve Maltepe yerleşkelerinde faaliyet göstermektedir. 2016-2017 eğitim öğretim yılında ilk öğrencilerini karşılayacak olan üniversite, çağdaş eğitim programları ve mezunlarının kalitesiyle fark yaratacak öncü üniversite olmayı vizyon edinmiştir. Ayrıca, yaşam boyu eğitim felsefesiyle insan gücü kalitesinin yükseltilmesine katkı sağlamak üniversite değerlerindendir.', 'istanbul-rumeli-universitesi', NULL, NULL, 'İstanbul', NULL, NULL);
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(159, 'İstanbul Sabahattin Zaim Üniversitesi', 'İstanbul Sabahattin Zaim Üniversitesi, Halkalı Cad. No:2 Halkalı - Küçükçekmece / İSTANBUL', NULL, 'http://www.izu.edu.tr', 'İstanbul Sabahattin Zaim Üniversitesi , 2010 yılında İstanbul\'da İlim Yayma Vakfı tarafından akademik ve fiziki alt yapısı ile öne çıkan, mezunları tercih edilen, yayınları başvuru kaynağı olan ve üniversitelerimiz arasında ilk yedide yer alan bir araştırma üniversitesi olma amacıyla kurulan bir vakıf üniversitesidir. Merkez kampüsü Halkalı\'da bulunan İzü\'nün Eyüp\'te de yerleşkesi bulunmaktadır. 2517 öğrencinin öğrenim gördüğü üniversitenin 8 fakülte ve 2 enstitüsünde 163 akademisyen görev almaktadır.', 'istanbul-sabahattin-zaim-universitesi', '28.7883359', '41.0336113', 'İstanbul', NULL, '/m/0zdnj1v');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(160, 'İstanbul Şehir Üniversitesi', 'Kuşbakışı Caddesi No: 27 34662, Altunizade – Üsküdar – İstanbul', NULL, 'http://www.sehir.edu.tr', 'İstanbul Şehir Üniversitesi, 1986 yılından bu yana Bilim ve Sanat Vakfı’na yön veren akademik arayışın içinden doğdu. Gerçek bir “vakıf üniversitesi” olarak yola çıkan İstanbul Şehir Üniversitesi, fiilî kuruluş çalışmalarına 2007 yılında başladı.Keşifsel bilginin üretimine ve yaygınlaştırılmasına katkıda bulunmayı amaçlayan İstanbul Şehir Üniversitesi, özgün, katılımcı, evrensel ve özgürlükçü perspektifiyle ve zenginleşen ekibiyle, 2010-2011 akademik yılında ilk öğrencileri ile buluştu. İstanbul Şehir Üniversitesi, Halen 6 fakülte, 3 enstitü çatısı altında 14 lisans, 15 lisansüstü programı ile Altunizade Doğu, Altunizade Batı ve Altunizade Güney Kampüsleri\'nde 188 akademik personeli ve 3393 öğrencisiyle faaliyetlerini sürdürüyor.İstanbul Şehir Üniversitesi misyonu; iyi, doğru ve güzeli aramak, insan, toplum ve doğayı anlamak, bu arayışın ürünü olan bilgileri tüm insanlıkla paylaşmaktır.', 'istanbul-sehir-universitesi', NULL, NULL, 'İstanbul', NULL, '/m/0dllgdd');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(161, 'İstanbul Ticaret Üniversitesi', 'Küçükyalı E5 Kavşağı İnönü Cad. No: 4, Küçükyalı 34840, İstanbul', NULL, 'http://www.ticaret.edu.tr', 'İstanbul Ticaret Üniversitesi, İstanbul Ticaret Odası Eğitim ve Sosyal Hizmetler Vakfı tarafından 2001 yılında akademik esnekliğini yitirmeden, bilgi çağının gerekliliği olan disiplinler arası çalışmaları bünyesinde yürütebilen; uluslararası bilimsel faaliyetlerde aktif ve belirleyici olarak yer alan, adını aldığı İstanbul’a yaraşır, yalnız ulusal düzeyde değil uluslararası düzeyde de saygınlığı kabul edilmiş, önde gelen akademik kurumların içinde yer alma vizyonuyla İstanbul\'da kurulmuştur. Üniversite\'nin yerleşkeleri Eminönü, Sütlüce, Küçükyalı ve Bursa\'da konumlanmıştır. 7.000 öğrencinin öğrenim gördüğü 6 fakülte (Fen Edebiyat, Ticari Bilimler, Hukuk, İletişim, Mühendislik ve Tasarım ile Uygulamalı Bilimler), İngilizce Hazırlık Bölümü ve 4 enstitü (Dış Ticaret, Sosyal Bilimler, Fen Bilimleri ve Finans Enstitüsü) ile araştırma ve uygulama merkezlerinde 300’ü aşkın akademisyen görev almaktadır.', 'istanbul-ticaret-universitesi', NULL, NULL, 'İstanbul', NULL, '/m/05b4f29');
INSERT INTO `university` (`id`, `name`, `address`, `logo`, `website`, `desc`, `url`, `longitude`, `latitude`, `city`, `type`, `social`) VALUES
	(162, 'İstinye Üniversitesi', 'Maltepe Mahallesi Edirne Çırpıcı Yolu No.9 Cevizlibağ / Topkapı / İstanbul / TÜRKİYE', NULL, 'http://www.istinye.edu.tr/', 'İstinye Üniversitesi, Türkiye’nin en büyük sağlık markası olan MLPCare Grubu’nun eğitime değerli bir yatırımıdır. Türkiye\'nin dört bir yanında 27 hastane ve 14 bin çalışanıyla hizmet veren bir sağlık grubu olarak 20 yılı aşkın sürede kazandığı bilgi ve birikimi, siz gençlerimizin eğitim ve öğretimi için kullanacaktır. İstinye Üniversitesi, bu yıl Tıp Fakültesi, Sağlık Bilimleri Fakültesi ve Sağlık Hizmetleri Meslek Yüksekokulu’yla eğitime başlayacak olup tüm öğrencilerine belli oranlarda burs verecektir. Ayrıca bütün öğrencilerini sağlık sigortasıyla güvenceye alacaktır.', 'istinye-universitesi', NULL, NULL, 'İstanbul', NULL, NULL);
